import { useState, useEffect } from 'react';
import { Heart, Sparkles } from 'lucide-react';

export default function BirthdayWebsite() {
    const [page, setPage] = useState(1);
    const [imageIndex, setImageIndex] = useState(0);
    const [showCake, setShowCake] = useState(false);
    const [particles, setParticles] = useState([]);
    const [heartBurst, setHeartBurst] = useState([]);
    const [isExploding, setIsExploding] = useState(false);
    const [selectedYear, setSelectedYear] = useState(null);
    const [currentPhotoIndex, setCurrentPhotoIndex] = useState(0);

    // Year-wise photo data
    const yearPhotos = {
        2022: [
            "https://images.unsplash.com/photo-1578632749014-ca77efd010e1?w=800&h=600&fit=crop",
            "https://images.unsplash.com/photo-1506744038136-46273834b3fb?w=800&h=600&fit=crop",
            "https://images.unsplash.com/photo-1518834107812-67b0b7c58434?w=800&h=600&fit=crop"
        ],
        2023: [
            "https://images.unsplash.com/photo-1529333166437-7750a6dd5a70?w=800&h=600&fit=crop",
            "https://images.unsplash.com/photo-1511988617509-a57c8a288659?w=800&h=600&fit=crop",
            "https://images.unsplash.com/photo-1530281700549-e82e7bf110d2?w=800&h=600&fit=crop",
            "https://images.unsplash.com/photo-1502086223501-7ea6ecd79368?w=800&h=600&fit=crop"
        ],
        2024: [
            "https://images.unsplash.com/photo-1543429776-2782fc586c3a?w=800&h=600&fit=crop",
            "https://images.unsplash.com/photo-1519839289307-8f8f6c1070e1?w=800&h=600&fit=crop",
            "https://images.unsplash.com/photo-1506260408121-e353d10b87c7?w=800&h=600&fit=crop"
        ],
        2025: [
            "https://images.unsplash.com/photo-1527549993546-2d1007e6e31e?w=800&h=600&fit=crop",
            "https://images.unsplash.com/photo-1519839289307-8f8f6c1070e1?w=800&h=600&fit=crop"
        ]
    };

    useEffect(() => {
        if (page === 3) {
            setTimeout(() => setShowCake(true), 500);

            const newParticles = Array.from({ length: 30 }, (_, i) => ({
                id: i,
                left: Math.random() * 100,
                delay: Math.random() * 5,
                duration: 3 + Math.random() * 4
            }));
            setParticles(newParticles);
        }
    }, [page]);

    const handleHeartClick = () => {
        setIsExploding(true);

        // Generate millions of hearts (well, 150 for performance!)
        const burstHearts = Array.from({ length: 150 }, (_, i) => ({
            id: i,
            left: 50,
            top: 50,
            angle: (Math.PI * 2 * i) / 150,
            distance: Math.random() * 80 + 40,
            scale: Math.random() * 0.5 + 0.3,
            duration: Math.random() * 0.5 + 1,
            rotation: Math.random() * 360
        }));

        setHeartBurst(burstHearts);

        // Transition to next page after explosion
        setTimeout(() => {
            setPage(2);
            setIsExploding(false);
            setHeartBurst([]);
        }, 1500);
    };

    const images = [
        "https://images.unsplash.com/photo-1529333166437-7750a6dd5a70?w=800&h=600&fit=crop",
        "https://images.unsplash.com/photo-1511988617509-a57c8a288659?w=800&h=600&fit=crop",
        "https://images.unsplash.com/photo-1530281700549-e82e7bf110d2?w=800&h=600&fit=crop",
        "https://images.unsplash.com/photo-1502086223501-7ea6ecd79368?w=800&h=600&fit=crop"
    ];

    const nextImage = () => {
        setImageIndex((prev) => (prev + 1) % images.length);
    };

    const prevImage = () => {
        setImageIndex((prev) => (prev - 1 + images.length) % images.length);
    };

    const handleYearClick = (year) => {
        setSelectedYear(year);
        setCurrentPhotoIndex(0);
    };

    const nextPhoto = () => {
        if (selectedYear && yearPhotos[selectedYear]) {
            setCurrentPhotoIndex((prev) => (prev + 1) % yearPhotos[selectedYear].length);
        }
    };

    const prevPhoto = () => {
        if (selectedYear && yearPhotos[selectedYear]) {
            setCurrentPhotoIndex((prev) => (prev - 1 + yearPhotos[selectedYear].length) % yearPhotos[selectedYear].length);
        }
    };

    if (page === 1) {
        return (
            <div className="min-h-screen relative overflow-hidden bg-gradient-to-br from-purple-900 via-pink-900 to-rose-900 flex items-center justify-center">
                {/* Animated background stars */}
                <div className="absolute inset-0 opacity-20">
                    {[...Array(50)].map((_, i) => (
                        <div
                            key={i}
                            className="absolute rounded-full bg-white"
                            style={{
                                width: Math.random() * 3 + 1 + 'px',
                                height: Math.random() * 3 + 1 + 'px',
                                left: Math.random() * 100 + '%',
                                top: Math.random() * 100 + '%',
                                animation: `twinkle ${Math.random() * 3 + 2}s infinite ${Math.random() * 2}s`
                            }}
                        />
                    ))}
                </div>

                <style>{`
          @keyframes twinkle {
            0%, 100% { opacity: 0.2; transform: scale(1); }
            50% { opacity: 1; transform: scale(1.5); }
          }
          @keyframes float {
            0%, 100% { transform: translateY(0px); }
            50% { transform: translateY(-20px); }
          }
          @keyframes heartbeat {
            0%, 100% { transform: scale(1); }
            25% { transform: scale(1.1); }
            50% { transform: scale(1); }
            75% { transform: scale(1.15); }
          }
          @keyframes gentleFloat {
            0%, 100% { transform: translateY(0px) translateX(0px); }
            25% { transform: translateY(-10px) translateX(5px); }
            50% { transform: translateY(-5px) translateX(-5px); }
            75% { transform: translateY(-15px) translateX(3px); }
          }
          @keyframes sparkleRotate {
            0% { transform: rotate(0deg); }
            100% { transform: rotate(360deg); }
          }
        `}</style>

                {/* Heart burst explosion */}
                {isExploding && heartBurst.map((heart) => (
                    <div
                        key={heart.id}
                        className="absolute text-red-500 pointer-events-none"
                        style={{
                            left: '50%',
                            top: '50%',
                            width: `${heart.scale * 40}px`,
                            height: `${heart.scale * 40}px`,
                            animation: `burst ${heart.duration}s ease-out forwards`,
                            '--angle': `${heart.angle}rad`,
                            '--distance': `${heart.distance}vw`,
                            '--rotation': `${heart.rotation}deg`,
                            opacity: 1,
                            filter: 'drop-shadow(0 0 10px rgba(239, 68, 68, 0.8))',
                            position: 'fixed',
                            transform: 'translate(-50%, -50%)'
                        }}
                    >
                        <Heart className="w-full h-full fill-current" />
                    </div>
                ))}

                <style>{`
          @keyframes burst {
            0% {
              transform: translate(-50%, -50%) rotate(0deg) scale(1);
              opacity: 1;
            }
            100% {
              transform: translate(calc(-50% + cos(var(--angle)) * var(--distance)), calc(-50% + sin(var(--angle)) * var(--distance))) rotate(var(--rotation)) scale(0.2);
              opacity: 0;
            }
          }
        `}</style>

                <div className="relative z-10 text-center px-4 max-w-4xl mx-auto">
                    {/* Title with perfect alignment */}
                    <div className="mb-16">
                        <h1 className="text-7xl sm:text-8xl md:text-9xl lg:text-[10rem] font-bold bg-gradient-to-r from-pink-200 via-purple-200 to-pink-200 bg-clip-text text-transparent tracking-wider leading-none"
                            style={{
                                textShadow: '0 0 60px rgba(236, 72, 153, 0.6)',
                                fontFamily: 'Georgia, serif',
                                animation: 'gentleFloat 4s ease-in-out infinite'
                            }}>
                            Khebdi Jannu
                        </h1>

                        {/* Sparkles decoration - perfectly centered */}
                        <div className="flex justify-center items-center gap-3 mt-8">
                            {[...Array(7)].map((_, i) => (
                                <Sparkles
                                    key={i}
                                    className="w-5 h-5 md:w-7 md:h-7 text-yellow-300"
                                    style={{
                                        animation: `twinkle ${1 + i * 0.2}s ease-in-out infinite ${i * 0.15}s, sparkleRotate ${3 + i}s linear infinite`,
                                        filter: 'drop-shadow(0 0 8px rgba(253, 224, 71, 0.8))'
                                    }}
                                />
                            ))}
                        </div>
                    </div>

                    {/* Heart button - perfectly centered */}
                    <div className="flex flex-col items-center justify-center">
                        <button
                            onClick={handleHeartClick}
                            disabled={isExploding}
                            className="group relative inline-flex items-center justify-center transform transition-all duration-300 hover:scale-110 disabled:cursor-not-allowed"
                        >
                            {/* Glow effect */}
                            <div className="absolute inset-0 bg-pink-500 rounded-full blur-3xl opacity-40 group-hover:opacity-60 transition-opacity"
                                style={{ width: '200px', height: '200px', margin: '-35px' }} />

                            {/* Main heart */}
                            <Heart
                                className="relative w-28 h-28 sm:w-32 sm:h-32 md:w-40 md:h-40 text-red-500 fill-red-500 cursor-pointer"
                                style={{
                                    animation: isExploding ? 'none' : 'heartbeat 1.5s ease-in-out infinite',
                                    filter: 'drop-shadow(0 0 30px rgba(239, 68, 68, 0.9))',
                                    transform: isExploding ? 'scale(1.5)' : 'scale(1)',
                                    opacity: isExploding ? 0 : 1,
                                    transition: 'all 0.3s ease-out'
                                }}
                            />
                        </button>

                        {/* Text below heart */}
                        <div className="mt-8">
                            <p className="text-2xl sm:text-3xl md:text-4xl text-pink-200 font-bold tracking-wide"
                                style={{
                                    textShadow: '0 0 20px rgba(251, 207, 232, 0.9)',
                                    fontFamily: 'Georgia, serif'
                                }}>
                                ✨ Tap on heart ✨
                            </p>
                        </div>
                    </div>
                </div>
            </div>
        );
    }

    if (page === 2) {
        return (
            <div className="min-h-screen bg-gradient-to-br from-rose-950 via-purple-950 to-indigo-950 flex items-center justify-center p-4 relative overflow-hidden">
                {/* Floating hearts background */}
                {[...Array(15)].map((_, i) => (
                    <div
                        key={i}
                        className="absolute text-pink-500 opacity-10"
                        style={{
                            left: Math.random() * 100 + '%',
                            top: Math.random() * 100 + '%',
                            fontSize: Math.random() * 30 + 20 + 'px',
                            animation: `float ${Math.random() * 4 + 3}s ease-in-out infinite ${Math.random() * 2}s`
                        }}
                    >
                        ❤️
                    </div>
                ))}

                <div className="max-w-6xl w-full relative z-10">
                    <h2 className="text-5xl md:text-7xl font-bold text-center mb-12 bg-gradient-to-r from-pink-300 via-purple-300 to-pink-300 bg-clip-text text-transparent"
                        style={{
                            textShadow: '0 0 30px rgba(236, 72, 153, 0.5)',
                            fontFamily: 'Georgia, serif'
                        }}>
                        Our Beautiful Memories ✨
                    </h2>

                    {/* Year Selection */}
                    <div className="flex justify-center gap-4 mb-12">
                        {Object.keys(yearPhotos).map((year) => (
                            <button
                                key={year}
                                onClick={() => handleYearClick(year)}
                                className={`px-6 py-3 rounded-full font-bold text-lg transition-all transform hover:scale-105 ${selectedYear === year
                                        ? 'bg-gradient-to-r from-pink-500 to-purple-500 text-white shadow-lg'
                                        : 'bg-white bg-opacity-20 text-pink-200 hover:bg-opacity-30'
                                    }`}
                            >
                                {year}
                            </button>
                        ))}
                    </div>

                    {/* Photo Gallery */}
                    {selectedYear ? (
                        <div className="relative bg-white bg-opacity-10 backdrop-blur-xl rounded-3xl shadow-2xl p-8 mb-8 border border-white border-opacity-20"
                            style={{ boxShadow: '0 20px 60px rgba(0, 0, 0, 0.5)' }}>
                            <h3 className="text-3xl font-bold text-center mb-6 text-pink-200">
                                Photos from {selectedYear}
                            </h3>

                            <div className="relative h-96 md:h-[500px] overflow-hidden rounded-2xl group">
                                <img
                                    src={yearPhotos[selectedYear][currentPhotoIndex]}
                                    alt={`Photo from ${selectedYear} - ${currentPhotoIndex + 1}`}
                                    className="w-full h-full object-cover transition-transform duration-700 group-hover:scale-105"
                                    style={{ filter: 'brightness(1.1) contrast(1.1)' }}
                                />
                                <div className="absolute inset-0 bg-gradient-to-t from-black via-transparent to-transparent opacity-40" />
                            </div>

                            <div className="flex justify-between items-center mt-8">
                                <button
                                    onClick={prevPhoto}
                                    className="bg-gradient-to-r from-pink-500 to-purple-500 text-white px-8 py-4 rounded-full hover:from-pink-600 hover:to-purple-600 transition-all font-bold shadow-lg hover:shadow-2xl transform hover:scale-105"
                                >
                                    ← Previous
                                </button>
                                <span className="text-pink-200 font-bold text-xl backdrop-blur-sm bg-white bg-opacity-10 px-6 py-2 rounded-full">
                                    {currentPhotoIndex + 1} / {yearPhotos[selectedYear].length}
                                </span>
                                <button
                                    onClick={nextPhoto}
                                    className="bg-gradient-to-r from-purple-500 to-pink-500 text-white px-8 py-4 rounded-full hover:from-purple-600 hover:to-pink-600 transition-all font-bold shadow-lg hover:shadow-2xl transform hover:scale-105"
                                >
                                    Next →
                                </button>
                            </div>
                        </div>
                    ) : (
                        <div className="text-center py-16">
                            <p className="text-2xl text-pink-200 mb-8">Select a year to view photos</p>
                        </div>
                    )}

                    {/* Original memories gallery button */}
                    <div className="text-center mt-8">
                        <button
                            onClick={() => setPage(3)}
                            className="relative bg-gradient-to-r from-pink-500 via-purple-500 to-pink-500 text-white px-12 py-5 rounded-full text-2xl font-bold hover:scale-105 transition-transform shadow-2xl overflow-hidden group"
                        >
                            <span className="relative z-10">Continue to Surprise 🎂</span>
                            <div className="absolute inset-0 bg-gradient-to-r from-purple-500 via-pink-500 to-purple-500 opacity-0 group-hover:opacity-100 transition-opacity" />
                        </button>
                    </div>
                </div>
            </div>
        );
    }

    if (page === 3) {
        return (
            <div className="min-h-screen bg-gradient-to-br from-amber-950 via-rose-950 to-purple-950 flex items-center justify-center p-4 relative overflow-hidden">
                {/* Floating particles */}
                {particles.map(particle => (
                    <div
                        key={particle.id}
                        className="absolute w-2 h-2 bg-yellow-300 rounded-full opacity-60"
                        style={{
                            left: particle.left + '%',
                            bottom: '-10%',
                            animation: `rise ${particle.duration}s ease-in ${particle.delay}s infinite`
                        }}
                    />
                ))}

                <style>{`
          @keyframes rise {
            0% { transform: translateY(0) rotate(0deg); opacity: 0; }
            10% { opacity: 0.6; }
            90% { opacity: 0.6; }
            100% { transform: translateY(-110vh) rotate(360deg); opacity: 0; }
          }
          @keyframes cakeRise {
            0% { transform: translateY(100vh) scale(0.5) rotateY(0deg); opacity: 0; }
            60% { transform: translateY(0) scale(1.1) rotateY(360deg); opacity: 1; }
            100% { transform: translateY(0) scale(1) rotateY(360deg); opacity: 1; }
          }
          @keyframes flame {
            0%, 100% { transform: scaleY(1) translateY(0); }
            50% { transform: scaleY(1.2) translateY(-2px); }
          }
        `}</style>

                <div className="max-w-4xl w-full relative z-10">
                    {/* 3D Animated Cake */}
                    <div className={`text-center mb-12 transition-all duration-1000 ${showCake ? 'opacity-100' : 'opacity-0'}`}
                        style={{
                            animation: showCake ? 'cakeRise 2.5s ease-out forwards' : 'none',
                            transformStyle: 'preserve-3d'
                        }}>
                        <div className="inline-block relative" style={{ perspective: '1000px' }}>
                            {/* Cake emoji with glow */}
                            <div className="text-9xl mb-4 relative"
                                style={{
                                    filter: 'drop-shadow(0 0 30px rgba(251, 191, 36, 0.8))',
                                    transform: 'rotateY(0deg)',
                                    transformStyle: 'preserve-3d'
                                }}>
                                🎂
                            </div>

                            {/* Animated candles */}
                            <div className="flex justify-center gap-3 mb-6">
                                {[1, 2, 3, 4, 5].map((i) => (
                                    <div key={i} className="relative">
                                        <div className="w-3 h-16 bg-gradient-to-b from-pink-400 to-pink-600 rounded shadow-lg"></div>
                                        <div className="absolute -top-4 left-1/2 transform -translate-x-1/2 w-5 h-8 bg-gradient-to-t from-orange-500 via-yellow-400 to-yellow-200 rounded-t-full"
                                            style={{
                                                animation: `flame 0.${i}s ease-in-out infinite`,
                                                filter: 'drop-shadow(0 0 10px rgba(251, 191, 36, 0.8))'
                                            }} />
                                    </div>
                                ))}
                            </div>
                        </div>

